exports.getUTCDate = function() {
  var now = new Date();
  now.setTime(now.getTime() - (now.getTimezoneOffset() * 60000));
  return now;
}

exports.parseUserId = function(str) {
	var at = str.indexOf('@', 1);
	var len = str.length;
	if (at == -1 || at >= (len - 1))
	  return null;
	var user = { };
	user.name = str.substring(0, at);
	user.realm = str.substring(at + 1, len);
	return user;
}